            var discount = $(el.find('.discount')).val();
            var item_id = $(el.find('.item')).val();

            $.ajax({
                url: '<?php echo e(route('product.quantity')); ?>',
                type: 'POST',
                data: {
                    "item_id": item_id,
                    "quantity":quantity,
                    "_token": "<?php echo e(csrf_token()); ?>",
                },
